import { createApp } from "vue";
import App from "./App.vue";
import router from "./router";
import store from "./store";

// Importar estilos y scripts de Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import * as bootstrap from "bootstrap";
window.bootstrap = bootstrap;

// Importar otros estilos y librerías
import "@fortawesome/fontawesome-free/css/all.min.css";
import "bootstrap-icons/font/bootstrap-icons.css";
import "./css/styles.css";

// Importar y registrar Cropper
import Cropper from "cropperjs";
import "cropperjs/dist/cropper.css";

store.dispatch("auth/validateToken").finally(() => {
  console.log("Bootstrap Loaded:", typeof bootstrap);

  const app = createApp(App);

  // Registrar Cropper como propiedad global
  app.config.globalProperties.$Cropper = Cropper;

  // Registrar Bootstrap como propiedad global en Vue
  app.config.globalProperties.$bootstrap = bootstrap;

  app.use(router);
  app.use(store);
  app.mount("#app");
});
